import {useEffect,useState} from 'react';
import products from './products';
import Cart from './cart';
import './App.css';

const App = ()=>{

  const [data,setData] = useState('')
  // const [isFilterd,setIsFiltered] = useState(false)
  const [filtereddata,setFilteredData] = useState([])

  useEffect(()=>{
    setData(products)
    setFilteredData(products)
  },[])
  const handleAddToCart = (index)=>{
    console.log(index)
  }
  const renderData = ()=>{
    return filtereddata.map((dataItem,index)=>{
      return(
        <div key={dataItem.id} className="box">
          {dataItem.isFreeShipping ? <span className="shipping-tag">Free Shipping</span> : null}
          <div className="image-container">
            <img src={process.env.PUBLIC_URL+dataItem.src_1} alt={dataItem.title} />
          </div>
          <p className="title">{dataItem.title}</p>
          <p>{dataItem.currencyFormat} {dataItem.price}</p>
          <p style={{'color':'grey'}}>or {dataItem.installments} x {dataItem.currencyFormat} {(dataItem.price/dataItem.installments).toFixed(2)}</p>
          <p>{dataItem.availableSizes.map(size=>size+",")}</p>
          <button onClick={handleAddToCart(index)}>Add To cart</button>
        </div>
      )
    })
  }
  const applyFilter = (e,size)=>{
    const allBtns = document.getElementsByClassName('btn')
    for (let i = 0; i < allBtns.length; i++) {
      allBtns[i].classList.remove('active');
    }
    e.currentTarget.classList.add('active')
    if(size === "ALL"){
      const filteredArray = [...data]
      setFilteredData(filteredArray)
    }
    else{
      const filteredArray = data.filter(dataItem=>dataItem.availableSizes.indexOf(size) !== -1)
      setFilteredData(filteredArray)
    }
    // setIsFiltered(true)
  }
  return (
    <div className="container">
      <Cart />
      {data === '' ? <center><div>loading....</div></center> : 
      <div>
        <center><p>Sizes:</p></center>
        <div className="filters">
          <button className="btn" onClick={e=>applyFilter(e,"XS")}>XS</button>
          <button className="btn" onClick={e=>applyFilter(e,"S")}>S</button>
          <button className="btn" onClick={e=>applyFilter(e,"M")}>M</button>
          <button className="btn" onClick={e=>applyFilter(e,"ML")}>ML</button>
          <button className="btn" onClick={e=>applyFilter(e,"L")}>L</button>
          <button className="btn" onClick={e=>applyFilter(e,"XL")}>XL</button>
          <button className="btn" onClick={e=>applyFilter(e,"XXL")}>XXL</button>
          <button className="btn active" onClick={e=>applyFilter(e,"ALL")}>ALL</button>
        </div>
        <center><p style={{'marginTop':'20px'}}>{filtereddata.length} product(s) found</p></center>
        <div className="box-container">{renderData()}</div>
      </div>}
    </div>
  )
}
export default App;
