import {useEffect,useState} from 'react';
import Cart from './cart'
import './App.css';
import products from './products';
const App = ()=>{
  const [data,setData] = useState('')
  useEffect(()=>{
    // console.log(products)
    setData(products)
  },[])
  const renderData = ()=>{
    return data.map(dataItem=>{
      return(
        <div key={dataItem.id}>
          {dataItem.isFreeShipping ? <span>Free Shipping</span> : null}
          <div>
            <img src={process.env.PUBLIC_URL+dataItem.src_1} alt={dataItem.title} />
          </div>
          <p>{dataItem.title}</p>
          <p>{dataItem.currencyFormat} {dataItem.price}</p>
          <p>or {dataItem.installments} x {dataItem.currencyFormat} {dataItem.price/dataItem.installments}</p>
          <button>Add To cart</button>
        </div>
      )
    })
  }
  return (
    <div className="container">
      {data === '' ? <center><div>loading....</div></center> : 
      <div>
        <center><p>{data.length} product(s) found</p></center>
        <div className="box-container">{renderData()}</div>
      </div>}
    </div>
  )
}
export default App;
