import React,{useState} from 'react';
import CartIcon from './shopping-cart.svg'
import './App.css';
const Cart = props=>{
    const [total,setTotal]=useState(0)
    return (
    <React.Fragment>
        <div className="header">
            <p>T-shirt Store</p>
            <span style={{'position':'relative'}}>
                <img src={CartIcon} alt="cart" height="30" width="30"/>
                <span className="count">{props.data.length}</span>
            </span>
        </div>
        <div className="cart-data">
            {props.data.map((dataItem)=>{
                return(
                    <div className="cart-item">
                            <img src={process.env.PUBLIC_URL+dataItem.src_1} alt={dataItem.title} />
                            <div className="content">
                                <p>{dataItem.title}</p>
                                <p style={{'color':'grey'}}>{dataItem.style}</p>
                                <p style={{'color':'grey'}}>Qunatity:1</p>
                            </div>
                        <span className="cart-item-price">${dataItem.price}</span>
                    </div>
                )
            })}
            <div style={{'padding':'5px 10px;','backgroundColor':'grey'}}>
                <p>Total:</p>
                <p>${total}</p>
            </div>
        </div>
    </React.Fragment>
    )
}
export default Cart;
